  {label: 'Lindenholt', value: 'Lindenholt', shortcut: ''},
];

var otherValuesNotUsedForNow = [
  ];

var dates = [
    {van : '1-10', tot: '2-10', item : {label: 'Nijmegen-Noord', value: 'Nijmegen-Noord', shortcut: ''}},
    {van : '1-10', tot: '2-10', item : {label: 'Nijmegen-Centrum', value: 'Nijmegen-Centrum', shortcut: ''}}
  ];
    var currentDate = new Date();
    dates.forEach(function(item, key) {
      var d1 = item.van.split("-");
      var d2 = item.tot.split("-");
      var from = new Date(new Date().getFullYear(), parseInt(d1[1])-1, d1[0]);
      var to = new Date(new Date().getFullYear(), parseInt(d2[1])-1, d2[0]);
      
      if(currentDate > from && currentDate < to) {
        defaultValues.push(item.item);
      }
    });

  values = defaultValues;

